import React from "react";
import Currentdate from "./Currentdate";
import WeatherIcon from "./WeatherIcon";
import WeatherTemperature from "./WeatherTemperature";
import "./Weather.css";

export default function CurrentWeatherinfo(props) {
  return (
    <div className="weatherinfo">
      <h1 className="mt-4">{props.info.city}</h1>
      <p>
        Last updated: <Currentdate date={props.info.date} />
      </p>

          <div className="clearfix">
            <span className="float-left icon" >
              <WeatherIcon code={props.info.icon} size={45} />
            </span>
            <span className="float-left">
              <WeatherTemperature celsius={props.info.temperature} />
            </span>
          </div>
          <div className="text-capitalize">{props.info.description}</div>
        </div>



  );
}
