import React from "react";
import WeatherIcon from "./WeatherIcon"

export default function ForecastDay(props){
     function maxTemperature(){
        let temperature = Math.round(props.forecast[0].temp.max)
        return `${temperature}`
     }

     function minTemperature() {
       let temperature = Math.round(props.forecast[0].temp.min);
       return `${temperature}`;
     }

     let days = [
       "Sunday",
       "Monday",
       "Tuesday",
       "Wednesday",
       "Thursday",
       "Friday",
       "Saturday",
     ];
     date = new Date(props)

    return (
      <div className="forecastday">
        <div className="weatherforecast-day">{props.forecast[0].dt}</div>
        <WeatherIcon code={props.forecast[0].weather[0].icon} size={20}/>{" "}
        <div className="weatherforecast-temperature">
          <span className="weatherforecast-temperature-max">
           { maxTemperature()}
          </span>{" "}
          <span className="weatherforecast-temperature-min">
            {minTemperature()}
          </span>
        </div>
      </div>
    );
}
