import React, { useState } from "react";
import WeatherIcon from "./WeatherIcon";
import axios from "axios";
import "./WeatherForecast.css";

export default function WeatherForecast(props) {
  let [loaded, setLoaded]= useState(false);
  let [forecastData, setForecastData]=useState("")

  function handleResponse(response) {
    console.log(response.data.daily[0].temp.min);
    console.log(response.data.daily[0].temp.max);
    console.log(response.data.daily[0].dt);
    setForecastData(response.data.daily)
    setLoaded(true)
  }



if(loaded){
return (
  <div className="weather-forecast">
    <div>
      <div className="weatherforecast-day">Thu</div>
      <WeatherIcon code={forecastData[0].weather[0].icon} size={20} />{" "}
      <div className="weatherforecast-temperature">
        <span className="weatherforecast-temperature-max"></span>{" "}
        <span className="weatherforecast-temperature-min">10</span>
      </div>
    </div>
  </div>
);
}else{
  let longitude = props.coordinates.lon;
  let latitude = props.coordinates.lat;
  let apiKey = "caa883a4a60d93878755b08a933f74ea";

  let apiUrl = `https://api.openweathermap.org/data/2.5/onecall?lat=${latitude}&lon=${longitude}&appid=${apiKey}&units=metric`;

  axios.get(apiUrl).then(handleResponse);
  return "loading"

}

}
