import React, { useState } from "react";
import "./Weather.css";
import axios from "axios";

export default function Weather() {
  let [ready, setReady] = useState(false);
  let [weatherdata, setWeatherdata] = useState({});

  function handleResponse(response) {
    console.log(response);
    setWeatherdata({
      temperature: Math.round(response.data.main.temp),
      description: response.data.weather[0].description,
      humidy: Math.round(response.data.main.humidity),
      wind: response.data.wind.speed, pressure: Math.round(response.data.main.pressure)  
    })
    setReady(true);
  };

  if (ready) {
    return (
      <div className="weather-app mt-4">
        <form>
          <div className="row">
            <div className="col-9">
              <input
                type="Search"
                placeholder="Enter city..."
                className="form-control"
              />
            </div>
            <div className="col-3">
              <input
                type="Submit"
                value="Search"
                className="btn btn-primary w-100"
              />
            </div>
          </div>
        </form>
        <div>
          <div className="row">
            <h1 className="mt-4">Cape Town</h1>
            <p>Last updated: Wednesday September 20 04:26</p>
            <div className="col-6 d-flex">
              <img
                src="https://ssl.gstatic.com/onebox/weather/64/sunny_s_cloudy.png"
                alt="weather condition"
                width="80px"
              />
              <h2>{weatherdata.temperature}Â°C</h2>
            </div>
            <div className="col-6">
              <ul className="list-unstyled">
                <li>Pressure: {weatherdata.pressure}</li>
                <li>Humidity: {weatherdata.humidity}</li>
                <li>Wind: {weatherdata.wind}</li>
              </ul>
            </div>
          </div>
        </div>
      </div>
    );
  } else {
    let city = "london";
    let apiKey = "caa883a4a60d93878755b08a933f74ea";
    let apiUrl = `https://api.openweathermap.org/data/2.5/weather?q=${city}&appid=${apiKey}&units=metric`;
    axios.get(apiUrl).then(handleResponse);
    return "loading....";
  }
}
