import React from "react";
import WeatherIcon from "./WeatherIcon";


export default function ForecastHourly(props){
    function Temperature() {
      let temperature = Math.round(props.hourlydata.temp);
      return `${temperature}`;
    };

    console.log(props.hourlydata.clouds)

    function hour() {
      let date = new Date(props.hourlydata.dt * 1000);
      let hour = date.getHours();
      return`${hour}`}


    return(
    <div className="weather-forecast">
        <div className="row">
          {props.hourlydata.map(function (hourlyforecast, index) {
            if (index < 5) {
              return (
                <div className="col" key={index}>
                  <div className="weatherforecast-hour">{hour()}</div>
                  <WeatherIcon
                    code={props.forecast.weather[0].icon}
                    size={20}
                  />
                  <div className="weatherforecast-temperature">
                    {Temperature()}
                  </div>
                </div>
              );
            } else {
              return null;
            }
          })}
        </div>
      </div>
    );}
